# Start the typing speed test
def start():
	startTime = time.time() # The starting time - we'll need this later
	timer = Timer(120.0, result) # 120 seconds timer
	timer.start()
	global inputs
	inputs = 0.0
	global right
	right = 0.0
	global wrong
	wrong = 0.0
	global keyStrokes
	keyStrokes = 0.0

	# The cool part
	# True while the timer is on
	while exit != 1: 
		# Time elapsed
		timeElapsed = (time.time() - startTime) 

		# Random word from the list
		word = words[randint(0,(len(words) - 1))] 
		print("\nType in: {}".format(word), end="")

		# Print how much time is left
		print("{0}           [Time left: {1} seconds]".format(' ' * (len(longestWord) - len(word)),
		round((120 - timeElapsed)), 2))

		userInput = input()
		strokes = len(userInput)
		# Measure keystrokes
		keyStrokes += strokes 
		inputs += 1
		if userInput == word:
			right += 1
		else:
			wrong += 1
	else:
		sys.exit(0)

